export class StudentComponent implements OnInit {
  routeAnimationsElements = ROUTE_ANIMATIONS_ELEMENTS;
  versions = env.versions;

  schoolArray: any = [];
  studentArray: any = [];
  semesterArray: any = [];
  maxSemester: number;
  termArray: any = [];
  maxTerm: any;
  maxCycleDay: number;
  periodArray: any = [];
  maxPeriod: number;
  courseSectionArray: any = [];
  courseGradeArray: any = [];
  maxGrade: number;
  roomNameArray: any = [];
  roomNumberArray: any = [];
  teacherArray: any = [];

  specialStudent = [];
  studentObj = [];
  maxColumn: number;
  totalAnalysis: number;
  gridCSS: string;
  chk: boolean;
  sp_color: boolean;
  sh_cou: boolean;

  selectTeacher: number;
  selectSemester: number;
  selectStudent: number;

  constructor(private route: ActivatedRoute) {
  }

  ngOnInit() {
    this.init();
    this.initFromGlobalData();
  }

  openLink(link: string) {
    window.open(link, '_blank');
  }
  init() {
    this.selectTeacher = -1;
    this.selectSemester = -1;
    this.selectStudent = -1;

    this.chk=true;
    this.sp_color=true;
    this.sh_cou=false;
  }
  initFromGlobalData() {
    let scheduleData = AppSettings.getScheduleData();
    this.schoolArray = [];
    this.studentArray = [];
    this.semesterArray = [];
    this.maxSemester = 0;
    this.termArray = [];
    this.maxTerm = 0;
    this.maxCycleDay = 0;
    this.periodArray = [];
    this.maxPeriod = 0;
    this.courseSectionArray = [];
    this.courseGradeArray = [];
    this.maxGrade = 0;
    this.roomNameArray = [];
    this.roomNumberArray = [];
    this.teacherArray = [];
    this.studentObj = [];
    this.maxColumn = 0;
    this.totalAnalysis = 0;

    if (scheduleData && scheduleData.length > 0) {
      for (let i = 0; i < scheduleData.length; i++) {
        let eObj = scheduleData[i];
        if (this.schoolArray.indexOf(eObj['school_name']) === -1) {
          this.schoolArray.push(eObj['school_name']);
        }
        if (this.studentArray.indexOf(eObj['student_number']) === -1) {
          this.studentArray.push(eObj['student_number']);
        }
        if (this.semesterArray.indexOf(eObj['semester']) === -1) {
          this.semesterArray.push(eObj['semester']);
          if (this.maxSemester < eObj['semester'])
            this.maxSemester = eObj['semester'];
        }
        if (this.termArray.indexOf(eObj['term']) === -1) {
          this.termArray.push(eObj['term']);
          if (this.maxTerm < eObj['term']) this.maxTerm = eObj['term'];
        }
        if (this.maxCycleDay < eObj['cycle_day'])
          this.maxCycleDay = eObj['cycle_day'];
        if (this.periodArray.indexOf(eObj['period']) === -1) {
          this.periodArray.push(eObj['period']);
          if (this.maxPeriod < eObj['period']) this.maxPeriod = eObj['period'];
        }
        if (this.courseSectionArray.indexOf(eObj['course_section']) === -1) {
          this.courseSectionArray.push(eObj['course_section']);
        }
        if (this.courseGradeArray.indexOf(eObj['course_grade']) === -1) {
          this.courseGradeArray.push(eObj['course_grade']);
          if (this.maxGrade < eObj['course_grade'])
            this.maxGrade = eObj['course_grade'];
        }
        if (this.roomNameArray.indexOf(eObj['room_name']) === -1) {
          this.roomNameArray.push(eObj['room_name']);
        }
        if (this.roomNumberArray.indexOf(eObj['room_number']) === -1) {
          this.roomNumberArray.push(eObj['room_number']);
        }
        if (this.teacherArray.indexOf(eObj['teacher_name']) === -1) {
          this.teacherArray.push(eObj['teacher_name']);
        }
      }

      this.teacherArray.sort();
      this.studentArray.sort();
      this.semesterArray.sort();
      this.termArray.sort();
      this.periodArray.sort();
      this.roomNameArray.sort();

      this.updateContent();
    }
  }

  changeTeacher($event) {
    this.updateContent();
  }
  changeSemester($event) {
    this.updateContent();
  }
  changeRoom($event) {
    this.updateContent();
  }
  updateContent() {
    let param = this.route.snapshot.queryParams["section"];
    let hasSection = false, sections;
    let scheduleData = AppSettings.getScheduleData();
    let tempData = {},
      count;
    let studentHash = {},
      periodHash = {},
      semesterHash = {},
      hashPeriodSemester = {};
    if (param != undefined){
      sections = param.split(",");
      hasSection = true;
    }

    for (let i = 0; i < scheduleData.length; i++) {
      let eObj = scheduleData[i];
      let check = true;
      if (
        this.selectTeacher != -1 &&
        this.selectTeacher != eObj['teacher_name']
      )
        check = false;
      if (this.selectSemester != -1 && this.selectSemester != eObj['semester'])
        check = false;
      if (this.selectStudent != -1 && this.selectStudent != eObj['student_number'])
        check = false;
      if (hasSection)
      {
        if (sections.indexOf(eObj['course_section']) == -1)
          check = false;
      }
      if (check) {
        let key =
          eObj['student_number'] +
          '|||' +
          eObj['semester'].toString() +
          '|||' +
          eObj['period'].toString();
        if (!tempData[key]) tempData[key] = [];
        tempData[key].push({
          course_section: eObj['course_section'],
          room_name: eObj['room_name'],
          teacher_name: eObj['teacher_name']
        });
      }
    }

    this.studentObj[0] = [];
    this.studentObj[0].push(['Student', "#FFFFFF"]);
    for (let i = 0; i < this.semesterArray.length; i++) {
      for (let j = 0; j < this.periodArray.length; j++) {
        let key = 'S' + (i + 1).toString() + ' P' + (j + 1).toString();
        this.studentObj[0].push([key, "#FFFFFF"]);
        hashPeriodSemester[key] = i * this.periodArray.length + j;
      }
    }
    for (let i = 0; i < this.studentArray.length; i++) {
      this.studentObj[i + 1] = [];
      this.studentObj[i + 1][0] = [this.studentArray[i], "#FFFFFF"];
      studentHash[this.studentArray[i]] = i;
    }
    for (let i = 0; i < this.semesterArray.length; i++) {
      semesterHash[this.semesterArray[i]] = i;
    }
    for (let i = 0; i < this.periodArray.length; i++) {
      periodHash[this.periodArray[i]] = i;
    }
    this.maxColumn = this.semesterArray.length * this.periodArray.length + 1;

    for (let each in tempData) {
      let split = each.split('|||');
      let obj = tempData[each];
      let indR = studentHash[split[0]];
      let indS = semesterHash[split[1]] + 1;
      let indP = periodHash[split[2]] + 1;
      let indSPKey = 'S' + indS.toString() + ' P' + indP.toString();
      let indSP = hashPeriodSemester[indSPKey];
      this.studentObj[indR + 1][indSP + 1] = tempData[each];
    }

    count = this.studentObj.length;
    // count = 2;
    this.specialStudent = [];
    for (let i = 1; i < count; i++) {
      let obj = this.studentObj[i],
        emptyCount = 0;
      for (let j = 1; j < this.maxColumn; j++) {
        let arr = obj[j];
        if (arr && arr.length > 1)
        {
          this.specialStudent.push({
            "Student" : this.studentObj[i][0][0], 
            "Semester-Period" : this.studentObj[0][j][0]
          });
        }
        if (arr && arr.length > 0) {
          let firstLine = '';
          let cs = {}, numberOfCS = 0;

          for (let k = 0; k < arr.length; k++) {
            let section = arr[k]['course_section'];
            if (!cs[section]) {
              numberOfCS ++;
              cs[section] = [];
            }
            cs[section].push([ arr[k]['teacher_name'], arr[k]['room_name'] ]);
          }
          const ordered = {};
          Object.keys(cs)
            .sort()
            .forEach(function(key) {
              ordered[key] = cs[key];
            });
          firstLine =  "";
          for (let each in ordered) {
            let names = ordered[each][0][0].split(" "), name;
            if (names.length > 1)
              name = names[0] + ' ' + names[1].substring(0, 1).toUpperCase() + '.';
            else
              name = ordered[each][0][0];
            firstLine +=  each + ", " + ordered[each][0][1] + "<br/>"+ name+"<br/>";
          }

          if (this.sp_color==true)
            obj[j] = [firstLine, this.getColor(arr.length), arr.length];
          else
            obj[j] = [firstLine, "#FFFFFF", arr.length];

        }
        if (!obj[j] || obj[j].length === 0) {
          obj[j] = ["", this.getColor(0), 0];
          emptyCount++;
        }
        obj[j].push(arr);
      }
      if (emptyCount == this.maxColumn - 1) {
        this.studentObj.splice(i, 1);
        i--;
        count--;
      }
    }
    // console.log(this.specialStudent);
    // console.log(this.studentObj);
    this.totalAnalysis = this.studentObj.length - 1;
    let min = Math.floor(100 / this.maxColumn) - 1;
    let max = Math.ceil(100 / this.maxColumn) - 1;
    this.gridCSS = 'repeat(auto-fit, minmax(' + min + '%,' + max + '%))';
  }
  exportData() {
    let data = [], count;
    let search = '<br/>';
    let replacement = '\r\n ';
    count = this.studentObj.length;
    for (let i = 1; i < count; i++) {
      let obj = {}, key, key1;
      for (let j = 0; j < this.maxColumn; j++) {
        key = this.studentObj[0][j][0];
        let rr = this.studentObj[i][j][0].split(search);
        if (j==0){
          obj[key] = this.studentObj[i][j][0];
        }
        if (j > 0){          

          if(this.studentObj[i][j][2]===0){
            key1=key+" Section";
            obj[key1]="";
            key1=key+" Room";
            obj[key1]="";
          } else{
            let st=rr[0].split(',');
            key1=key+" Section";
            obj[key1]=st[0];
            key1=key+" Room";
            obj[key1]=st[1];
          }
          key1 = key + " Teacher";
          if (this.studentObj[i][j][2] === 0)
            obj[key1] = "";
          else
            obj[key1] = rr[1];
        }
      }
      data.push(obj);
    }
    if (data.length > 0){
      ExcelService.exportAsExcelFile(
        data,
        'Student - Semester/Period Analysis',
        false
      );
      ExcelService.exportAsExcelFile(
        this.specialStudent,
        'Special Students List',
        false
      );
    }
  }
  getColor(num) {
    return "#FFFFFF";
    if(this.chk==false) return "#FFFFFF";


    let c = num / 20 * 100;
    if (c === 0)
      return "#FFC7CE";
    else if (c < 50)
      return "#E26B00";
    else if (c < 100)
      return "#FFFF00";
    else
      return "#FFFFFF";
  }

  setColor($event){
    if (this.totalAnalysis==0) return;
    this.chk=!this.chk;
    this.updateContent();
  }

  showCourse($event){
    if (this.totalAnalysis==0) return;
    this.sh_cou=!this.sh_cou;
    this.updateContent();
  }


}


